mapper.select:
SELECT a.id, a.userId, a.title, a.content, a.date, a.publicScope, a.recommend, b.path, GROUP_CONCAT(b.fileName ORDER BY b.fileIndex) AS fileNames FROM feeds a INNER JOIN images b ON (a.id = b.feedId) AND (a.userId = #{userId}) WHERE a.publicScope = 'ALL'
OR a.publicScope = 'FRIENDS'
OR a.publicScope = 'ME'
AND a.id<#{pagination.cursor}
GROUP BY a.id ORDER BY a.id DESC LIMIT #{pagination.listSize}

SELECT a.id, a.userId, a.title, a.content, a.date, a.publicScope, a.recommend, b.path, GROUP_CONCAT(b.fileName ORDER BY b.fileIndex) AS fileNames FROM feeds a INNER JOIN images b ON (a.id = b.feedId) INNER JOIN friends c ON(a.userId = c.userId) AND (c.targetId = #{userId}) WHERE a.publicScope = 'ALL' OR a.publicScope = 'FRIENDS'
AND a.id<#{pagination.cursor}
GROUP BY a.id ORDER BY a.id DESC LIMIT #{pagination.listSize}
