mapper.update:
UPDATE users
SET nickname = #{nickname},
email = #{email},
birth = #{birth}
WHERE id = #{id}

mapper.select:
SELECT id, userId, name, nickname, email, birth
FROM users
WHERE userId = #{userId} AND password = #{password}

